choose.when:
			select ew.employee_code, e.employee_name
			from employee_work ew, employee e, payment p, department d, site s
			where ew.employee_code = e.employee_code
            and p.site_code = d.site_code
            and d.site_code = s.site_code
            and d.department_code = ew.department_code
            and p.job_class_code = ew.job_class_code
   		    and p.payment_code = #{Ppayment_code}  
   		    and s.site_name = #{searchSite}	
   		    and d.depart_name = #{searchDetail,jdbcType=VARCHAR}

			select ew.employee_code, e.employee_name
			from employee_work ew, employee e, payment p, work_group w, site s 
			where ew.employee_code = e.employee_code
            and p.site_code = w.site_code
            and w.site_code = s.site_code
            and w.work_group_code = ew.work_group_code
            and p.job_class_code = ew.job_class_code
   		    and p.payment_code = #{Ppayment_code}  
   		    and s.site_name = #{searchSite}	
   		    and w.work_group_name = #{searchDetail,jdbcType=VARCHAR}

			select ew.employee_code, e.employee_name
			from employee_work ew, employee e, payment p, prject pj, site s 
			where ew.employee_code = e.employee_code
            and p.site_code = w.site_code
            and pj.site_code = s.site_code
            and pj.project_code = ew.project_code
            and p.job_class_code = ew.job_class_code
   		    and p.payment_code = #{Ppayment_code}  
   		    and s.site_name = #{searchSite}	
   		    and pj.project_name = #{searchDetail,jdbcType=VARCHAR}
