mapper.select:
	
		select payment_receipt_code, payment_receipt_item, payment_receipt_price, payment_code, employee_code
		from payment_receipt
		where payment_code =  #{y}
        and employee_code =  #{x}

choose.when:
			select ew.employee_code, e.employee_name, p.payment_code
			from employee_work ew, employee e, payment p, department d, site s, selection_of_payment sp
			where ew.employee_code = e.employee_code
            and sp.site_code = d.site_code
            and d.site_code = s.site_code
            and d.department_code = ew.department_code
            and sp.job_class_code = ew.job_class_code
   		    and p.payment_code = #{Ppayment_code} 
   		    and s.site_name = #{searchSite}	
   		    and d.depart_name = #{searchDetail,jdbcType=VARCHAR}

			select ew.employee_code, e.employee_name, p.payment_code
			from employee_work ew, employee e, payment p, work_group w, site s, selection_of_payment sp 
			where ew.employee_code = e.employee_code
            and sp.site_code = w.site_code
            and w.site_code = s.site_code
            and w.work_group_code = ew.work_group_code
            and sp.job_class_code = ew.job_class_code
   		    and p.payment_code = #{Ppayment_code}  
   		    and s.site_name = #{searchSite}	
   		    and w.work_group_name = #{searchDetail,jdbcType=VARCHAR}

			select ew.employee_code, e.employee_name, p.payment_code
			from employee_work ew, employee e, payment p, prject pj, site s, selection_of_payment sp 
			where ew.employee_code = e.employee_code
            and sp.site_code = w.site_code
            and pj.site_code = s.site_code
            and pj.project_code = ew.project_code
            and sp.job_class_code = ew.job_class_code
   		    and p.payment_code = #{Ppayment_code}  
   		    and s.site_name = #{searchSite}	
   		    and pj.project_name = #{searchDetail,jdbcType=VARCHAR}

mapper.update:
update payment_receipt
set payment_receipt_item = #{payment_receipt_item}
   , payment_receipt_price = #{payment_receipt_price}
   , upt_user_id = #{user}
   , upt_date = sysdate
where payment_receipt_code = #{payment_receipt_code}
