mapper.select:
select c.department_code as DEPARTMENT_CODE, c.department_name as DEPARTMENT_NAME, a.employee_code as EMPLOYEE_CODE, a.employee_name as EMPLOYEE_NAME, z."basic_SALARY" as BASIC_SALARY,z."position_SALARY" as POSITION_SALARY , z."bonus_SALARY" as BONUS_SALARY from employee a,(select x.employee_code, sum(case when x.PAYMENT_RECEIPT_ITEM='기본급' then x.PAYMENT_RECEIPT_PRICE end) as "basic_SALARY", sum(case when x.PAYMENT_RECEIPT_ITEM='직책수당' then x.PAYMENT_RECEIPT_PRICE end) as "position_SALARY", sum(case when x.PAYMENT_RECEIPT_ITEM='상여급' then x.PAYMENT_RECEIPT_PRICE end) as "bonus_SALARY" from payment_receipt x
where x.payment_code = (select max(g.payment_code) from payment_receipt g)
where x.payment_code = #{Ppayment_code}
group by employee_code) z, department c, employee_work d, site o where a.employee_code = z.employee_code and c.department_code=d.department_code and d.employee_code=a.employee_code and o.site_code = c.site_code
and o.site_name = #{searchSite}
and c.department_name = #{DeptList}
